sys_variable_value.value:
(function execute(inputs, outputs) {

    var Response = false;
    
    var atualizarAuditoriaCreditos = new global.nowProUtils();
    Response = atualizarAuditoriaCreditos.atualizarAuditoriaCreditos(inputs.parameter_nome, inputs.parameter_quantidade_parcelas, inputs.parameter_solicitacao_credito, inputs.parameter_valor_credito);
    
    outputs.parameter_response = Response;

})(inputs, outputs);

